import React, { useRef, useEffect, useState } from "react";
import {
  rootStyle,
  chatContainer,
  messagesArea,
  inputBar,
  inputWrapper,
  sidebar,
  channelList,
  channelItem,
  createBtn,
  logoutBtn,
  usernameBox,
  chatTitle,
  typing,
  input as inputStyle,
} from "./styles/chatStyles";
import axios from "axios";
import io from "socket.io-client";

const API_URL = "http://localhost:5000/api";

function parseToken(token) {
  if (!token) return "";
  try {
    const t = token.split(".")[1].replace(/-/g, "+").replace(/_/g, "/");
    const n = decodeURIComponent(
      atob(t)
        .split("")
        .map((c) => "%" + ("00" + c.charCodeAt(0).toString(16)).slice(-2))
        .join("")
    );
    return JSON.parse(n).username || "";
  } catch {
    return "";
  }
}

function App() {
  const [token, setToken] = useState(localStorage.getItem("token"));
  const [channels, setChannels] = useState([]);
  const [selectedChannel, setSelectedChannel] = useState(null);
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState("");
  const [typing, setTyping] = useState("");
  const [showCreate, setShowCreate] = useState(false);
  const [newChannel, setNewChannel] = useState("");
  const [authMode, setAuthMode] = useState("login");
  const [login, setLogin] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");
  const [username, setUsername] = useState("");
  const socketRef = useRef(null);
  const messagesEndRef = useRef(null);

  useEffect(() => {
    setUsername(parseToken(token));
  }, [token]);

  useEffect(() => {
    if (!token) return;
    axios
      .get(`${API_URL}/channels`, {
        headers: { Authorization: `Bearer ${token}` },
      })
      .then((res) => setChannels(res.data))
      .catch(() => setChannels([]));

    socketRef.current = io("http://localhost:5000", {
      auth: { token },
    });

    socketRef.current.on("message", (msg) => {
      setMessages((prev) =>
        msg.channel === selectedChannel ? [...prev, msg] : prev
      );
    });

    socketRef.current.on("typing", (e) => setTyping(`${e.user} печатает...`));

    const handleNewChannel = () => window.location.reload();
    socketRef.current.on("new-channel", handleNewChannel);

    return () => {
      socketRef.current && socketRef.current.disconnect();
      socketRef.current && socketRef.current.off("new-channel", handleNewChannel);
    };
    // eslint-disable-next-line
  }, [token]);

  useEffect(() => {
    if (token && selectedChannel) {
      axios
        .get(`${API_URL}/messages/${selectedChannel}`, {
          headers: { Authorization: `Bearer ${token}` },
        })
        .then((res) => setMessages(res.data));
      socketRef.current && socketRef.current.emit("join", selectedChannel);
    }
  }, [token, selectedChannel]);

  useEffect(() => {
    messagesEndRef.current &&
      messagesEndRef.current.scrollIntoView({ behavior: "smooth" });
  }, [messages]);

  useEffect(() => {
    if (!socketRef.current) return;
    const handler = (msg) => {
      if (msg.channel === selectedChannel) setMessages((prev) => [...prev, msg]);
    };
    socketRef.current.on("message", handler);
    return () => socketRef.current.off("message", handler);
  }, [selectedChannel]);

  const handleCreateChannel = async () => {
    if (!newChannel.trim()) return;
    try {
      const t = parseToken(token);
      const res = await axios.post(
        `${API_URL}/channels`,
        { name: newChannel, members: [t] },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      setShowCreate(false);
      setNewChannel("");
      const chs = await axios.get(`${API_URL}/channels`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setChannels(chs.data);
      setSelectedChannel(res.data._id);
      socketRef.current && socketRef.current.emit("join", res.data._id);
      socketRef.current && socketRef.current.emit("new-channel");
    } catch {
      alert("Ошибка создания канала");
    }
  };

  const handleSend = () => {
    if (!input.trim() || !selectedChannel) return;
    const t = parseToken(token);
    const msg = { text: input, sender: t, channel: selectedChannel };
    socketRef.current && socketRef.current.emit("join", selectedChannel);
    socketRef.current.emit("message", msg);
    setInput("");
  };

  const handleAuth = async () => {
    setError("");
    try {
      if (authMode === "register") {
        await axios.post(`${API_URL}/register`, { username: login, password });
      }
      const res = await axios.post(`${API_URL}/login`, { username: login, password });
      localStorage.setItem("token", res.data.token);
      setToken(res.data.token);
    } catch (err) {
      setError(
        err.response && err.response.data && err.response.data.error
          ? err.response.data.error
          : "Неверный логин или пароль, либо пользователь не зарегистрирован"
      );
    }
  };

  if (!token) {
    return (
      <div style={rootStyle}>
        <div style={chatContainer}>
          <div style={chatStyles.authContainer}>
            <div style={chatStyles.authTitle}>
              {authMode === "register" ? "Регистрация" : "Вход"}
            </div>
            {error && <div style={chatStyles.error}>{error}</div>}
            <input
              style={chatStyles.authInput}
              placeholder="Имя"
              value={login}
              onChange={e => setLogin(e.target.value)}
            />
            <input
              style={chatStyles.authInput}
              placeholder="Пароль"
              type="password"
              value={password}
              onChange={e => setPassword(e.target.value)}
            />
            <button style={chatStyles.authBtn} onClick={handleAuth}>
              {authMode === "register" ? "Зарегистрироваться" : "Войти"}
            </button>
            <button
              style={chatStyles.switchBtn}
              onClick={() => setAuthMode(authMode === "register" ? "login" : "register")}
            >
              {authMode === "register" ? "Войти" : "Регистрация"}
            </button>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div style={rootStyle}>
      <div style={sidebar}>
        <div style={chatStyles.sidebarTitle}>Sechat.ru</div>
        <div style={channelList}>
          <div style={{ fontWeight: 600, color: "#fff", marginBottom: 10 }}>Каналы</div>
          {channels.length === 0 ? (
            <div style={{ color: "#b2bec3", marginBottom: 8 }}>
              Нет доступных каналов
            </div>
          ) : (
            <>
              {channels.map((ch) => (
                <div
                  key={ch._id}
                  style={channelItem(selectedChannel === ch._id)}
                  onClick={() => setSelectedChannel(ch._id)}
                >
                  {ch.name}
                </div>
              ))}
            </>
          )}
          <button
            style={createBtn}
            onClick={() => setShowCreate((v) => !v)}
          >
            {showCreate ? "Скрыть создание" : "Создать канал"}
          </button>
          {showCreate && (
            <div style={{ marginTop: 10 }}>
              <input
                style={inputStyle}
                placeholder="Название канала"
                value={newChannel}
                onChange={e => setNewChannel(e.target.value)}
              />
              <button style={createBtn} onClick={handleCreateChannel}>
                Создать
              </button>
            </div>
          )}
        </div>
        <button
          style={logoutBtn}
          onClick={() => {
            localStorage.removeItem("token");
            window.location.reload();
          }}
        >
          Выйти
        </button>
      </div>
      <div style={chatContainer}>
        {username && <div style={usernameBox}>{username}</div>}
        <div style={chatTitle}>Чат</div>
        <div style={messagesArea}>
          {messages.map((msg) => {
            const isMine = msg.sender === username;
            // Формат времени
            const time = msg.createdAt ? new Date(msg.createdAt).toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' }) : '';
            return (
              <div key={msg._id} style={chatStyles.messageRow(isMine)}>
                <div style={chatStyles.message(isMine)}>
                  {/* Только для чужих сообщений показываем имя */}
                  {!isMine && (
                    <span style={chatStyles.messageSender}>
                      {msg.sender}:
                    </span>
                  )}
                  {msg.text}
                  {msg.fileUrl && (
                    <a
                      href={msg.fileUrl}
                      target="_blank"
                      rel="noopener noreferrer"
                      style={{
                        color: "#00c3ff",
                        marginLeft: 8,
                        textDecoration: "underline",
                        fontWeight: 500,
                      }}
                    >
                      [файл]
                    </a>
                  )}
                  {/* Время сообщения под текстом, меньшим шрифтом */}
                  <div style={{ color: "#b2bec3", fontSize: 11, marginTop: 4, textAlign: isMine ? "right" : "left" }}>
                    {time}
                  </div>
                </div>
              </div>
            );
          })}
          <div ref={messagesEndRef} />
        </div>
        <div style={typing}>{typing}</div>
        {/* Поле ввода закреплено внизу */}
        <div style={inputBar}>
          <div style={inputWrapper}>
            <input
              style={inputStyle}
              value={input}
              onChange={e => setInput(e.target.value)}
              onKeyDown={e => {
                if (selectedChannel) {
                  socketRef.current.emit("typing", { channel: selectedChannel });
                  if (e.key === "Enter") handleSend();
                }
              }}
              disabled={!selectedChannel}
              placeholder={
                selectedChannel
                  ? "Введите сообщение..."
                  : "Выберите канал"
              }
            />
            <button
              style={chatStyles.sendBtn}
              onClick={handleSend}
              disabled={!selectedChannel || !input.trim()}
            >
              Отправить
            </button>
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
