import React, { useState, useEffect, useRef } from "react";
import axios from "axios";
import { io } from "socket.io-client";

const API_URL = "http://localhost:5000/api";

export default function App() {
  const [token, setToken] = useState(localStorage.getItem("token"));
  const [channels, setChannels] = useState([]);
  const [currentChannel, setCurrentChannel] = useState(null);
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState("");
  const [typing, setTyping] = useState("");
  const [showCreate, setShowCreate] = useState(false);
  const [newChannelName, setNewChannelName] = useState("");
  const [authMode, setAuthMode] = useState("login"); // "login" | "register"
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");
  const socketRef = useRef(null);

  // Функция для корректного декодирования JWT с поддержкой Unicode
  function parseJwt(token) {
    const base64Url = token.split(".")[1];
    const base64 = base64Url.replace(/-/g, "+").replace(/_/g, "/");
    const jsonPayload = decodeURIComponent(
      atob(base64)
        .split("")
        .map((c) => "%" + ("00" + c.charCodeAt(0).toString(16)).slice(-2))
        .join("")
    );
    return JSON.parse(jsonPayload);
  }

  // Загружаем список каналов при загрузке и при изменении токена
  useEffect(() => {
    if (token) {
      axios
        .get(`${API_URL}/channels`, {
          headers: { Authorization: `Bearer ${token}` },
        })
        .then((res) => setChannels(res.data))
        .catch(() => setChannels([]));
      socketRef.current = io("http://localhost:5000", {
        auth: { token },
      });
      socketRef.current.on("message", (msg) => {
        setMessages((prev) =>
          msg.channel === currentChannel ? [...prev, msg] : prev
        );
      });
      socketRef.current.on("typing", (e) => setTyping(`${e.user} печатает...`));
      return () => {
        socketRef.current && socketRef.current.disconnect();
      };
    }
  }, [token]);

  // Загружаем сообщения выбранного канала
  useEffect(() => {
    if (token && currentChannel) {
      axios
        .get(`${API_URL}/messages/${currentChannel}`, {
          headers: { Authorization: `Bearer ${token}` },
        })
        .then((res) => setMessages(res.data));
      if (socketRef.current) socketRef.current.emit("join", currentChannel);
    }
  }, [token, currentChannel]);

  // Создание канала
  const handleCreateChannel = async () => {
    if (!newChannelName.trim()) return;
    try {
      const user = parseJwt(token).username;
      // Передаем массив с текущим пользователем
      let t = parseJwt(token).username;
      if (typeof t !== "string") {
        if (typeof Buffer !== "undefined" && Buffer.isBuffer(t)) {
          t = Buffer.from(t).toString("utf8");
        } else {
          t = String(t);
        }
      }
      t = t.trim();
      const res = await axios.post(
        `${API_URL}/channels`,
        { name: newChannelName, members: [t] },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      setShowCreate(false);
      setNewChannelName("");
      // Обновить список каналов
      const channelsRes = await axios.get(`${API_URL}/channels`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setChannels(channelsRes.data);
      setCurrentChannel(res.data._id);
      socketRef.current && socketRef.current.emit("join", res.data._id);
    } catch {
      alert("Ошибка создания канала");
    }
  };

  // Отправка сообщения
  const handleSend = () => {
    if (!input.trim() || !currentChannel) return;
    const user = parseJwt(token).username;
    const msg = {
      text: input,
      sender: user,
      channel: currentChannel,
    };
    // Перед отправкой сообщения обязательно join в канал
    if (socketRef.current) socketRef.current.emit("join", currentChannel);
    // Отправляем сообщение через сокет
    socketRef.current.emit("message", msg);
    // Сразу добавляем сообщение в локальный стейт
    setMessages((prev) => [...prev, { ...msg, _id: Date.now() }]);
    setInput("");
  };

  // Аутентификация
  const handleAuth = async () => {
    setError("");
    try {
      if (authMode === "register") {
        await axios.post(`${API_URL}/register`, { username, password });
      }
      const res = await axios.post(`${API_URL}/login`, { username, password });
      localStorage.setItem("token", res.data.token);
      setToken(res.data.token);
    } catch (err) {
      // Показываем ошибку с сервера, если есть
      if (err.response && err.response.data && err.response.data.error) {
        setError(err.response.data.error);
      } else {
        setError(
          "Неверный логин или пароль, либо пользователь не зарегистрирован"
        );
      }
    }
  };

  if (!token) {
    return (
      <div>
        <h2>{authMode === "register" ? "Регистрация" : "Вход"}</h2>
        {error && <div style={{ color: "red" }}>{error}</div>}
        <input
          placeholder="Имя"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
        />
        <input
          placeholder="Пароль"
          type="password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
        />
        <button onClick={handleAuth}>
          {authMode === "register" ? "Зарегистрироваться" : "Войти"}
        </button>
        <button onClick={() => setAuthMode(authMode === "register" ? "login" : "register")}>
          {authMode === "register" ? "Войти" : "Регистрация"}
        </button>
      </div>
    );
  }

  return (
    <div style={{ display: "flex" }}>
      <div style={{ width: 220, paddingRight: 10, borderRight: "1px solid #ccc" }}>
        <h3>Каналы</h3>
        {channels.length === 0 ? (
          <div>
            <div style={{ color: "#888", marginBottom: 8 }}>
              Нет доступных каналов
            </div>
            {showCreate ? (
              <div>
                <input
                  placeholder="Название канала"
                  value={newChannelName}
                  onChange={(e) => setNewChannelName(e.target.value)}
                  style={{ width: "90%" }}
                />
                <button onClick={handleCreateChannel} style={{ marginTop: 4 }}>
                  Создать
                </button>
                <button
                  onClick={() => setShowCreate(false)}
                  style={{ marginLeft: 4 }}
                >
                  Отмена
                </button>
              </div>
            ) : (
              <button onClick={() => setShowCreate(true)}>
                Создать канал
              </button>
            )
            }
          </div>
        ) : (
          <>
            {channels.map((ch) => (
              <div
                key={ch._id}
                onClick={() => setCurrentChannel(ch._id)}
                style={{
                  cursor: "pointer",
                  background: currentChannel === ch._id ? "#eee" : "",
                  padding: "2px 0",
                }}
              >
                {ch.name}
              </div>
            ))}
            <button
              style={{ marginTop: 10 }}
              onClick={() => setShowCreate((v) => !v)}
            >
              {showCreate ? "Скрыть создание" : "Создать канал"}
            </button>
            {showCreate && (
              <div style={{ marginTop: 4 }}>
                <input
                  placeholder="Название канала"
                  value={newChannelName}
                  onChange={(e) => setNewChannelName(e.target.value)}
                  style={{ width: "90%" }}
                />
                <button onClick={handleCreateChannel} style={{ marginTop: 4 }}>
                  Создать
                </button>
              </div>
            )}
          </>
        )}
        <button
          style={{ marginTop: 20, color: "red" }}
          onClick={() => {
            localStorage.removeItem("token");
            window.location.reload();
          }}
        >
          Выйти
        </button>
      </div>
      <div style={{ flex: 1, marginLeft: 20 }}>
        <h3>Чат</h3>
        <div style={{ height: 300, overflowY: "auto", border: "1px solid #ccc" }}>
          {messages.map((m) => (
            <div key={m._id}>
              <b>{m.sender}:</b>{" "}
              {m.text}
              {m.fileUrl && (
                <a href={m.fileUrl} target="_blank" rel="noopener noreferrer">
                  [файл]
                </a>
              )}
            </div>
          ))}
        </div>
        <div>{typing}</div>
        <input
          value={input}
          onChange={(e) => setInput(e.target.value)}
          onKeyDown={(e) => {
            if (currentChannel) {
              socketRef.current.emit("typing", { channel: currentChannel });
              if (e.key === "Enter") handleSend();
            }
          }}
          disabled={!currentChannel}
          placeholder={
            currentChannel
              ? "Введите сообщение..."
              : "Выберите канал"
          }
        />
        <button
          onClick={handleSend}
          disabled={!currentChannel || !input.trim()}
        >
          Отправить
        </button>
      </div>
    </div>
  );
}
