export const page = {
  position: "fixed",
  top: 0,
  left: 0,
  width: "100vw",
  height: "100vh",
  background: "linear-gradient(120deg, #232526 0%, #414345 100%)",
  fontFamily: "'Segoe UI', 'Roboto', Arial, sans-serif",
  color: "#f5f6fa",
  display: "flex",
  alignItems: "stretch",
  justifyContent: "center",
};

export const sidebar = {
  width: 260,
  background: "rgba(30,32,34,0.98)",
  borderRight: "1px solid #232526",
  boxShadow: "2px 0 12px 0 rgba(0,0,0,0.12)",
  padding: "32px 18px 18px 18px",
  display: "flex",
  flexDirection: "column",
  minHeight: "100vh",
  position: "relative", // добавлено
};

export const sidebarTitle = {
  fontWeight: 700,
  fontSize: 22,
  marginBottom: 18,
  letterSpacing: 1,
  color: "#00c3ff",
  textShadow: "0 2px 8px #0002",
};

export const channelList = {
  flex: 1,
  overflowY: "auto",
  marginBottom: 18,
  paddingBottom: 60, // добавлено, чтобы не перекрывалось кнопкой "Выйти"
};

export const channelItem = isActive => ({
  cursor: "pointer",
  background: isActive ? "linear-gradient(90deg,#00c3ff33,#3a7bd5cc)" : "transparent",
  color: isActive ? "#fff" : "#b2bec3",
  borderRadius: 8,
  padding: "8px 12px",
  marginBottom: 6,
  fontWeight: isActive ? 600 : 400,
  boxShadow: isActive ? "0 2px 8px #00c3ff22" : "none",
  transition: "background 0.2s, color 0.2s",
  border: "none",
});

export const createBtn = {
  background: "linear-gradient(90deg,#00c3ff,#3a7bd5)",
  color: "#fff",
  border: "none",
  borderRadius: 8,
  padding: "8px 0",
  marginTop: 10,
  fontWeight: 600,
  fontSize: 15,
  cursor: "pointer",
  boxShadow: "0 2px 8px #00c3ff33",
  transition: "background 0.2s, box-shadow 0.2s",
};

export const logoutBtn = {
  marginTop: "auto", // теперь всегда внизу
  background: "none",
  color: "#ff7675",
  border: "1px solid #ff7675",
  borderRadius: 8,
  padding: "8px 0",
  fontWeight: 600,
  fontSize: 15,
  cursor: "pointer",
  transition: "background 0.2s, color 0.2s",
  position: "sticky", // закрепить
  bottom: 10,
  width: "100%",
  zIndex: 2,
  backgroundClip: "padding-box",
};

export const chatContainer = {
  flex: 1,
  display: "flex",
  flexDirection: "column",
  minHeight: 0,
  height: "100%",
  position: "relative",
  padding: "24px 0 8px 0",
};

export const usernameBox = {
  position: "absolute",
  top: -28,
  right: 0,
  color: "#00c3ff",
  fontWeight: "bold",
  fontSize: 17,
  padding: "4px 16px",
  background: "rgba(30,32,34,0.85)",
  borderRadius: 16,
  boxShadow: "0 2px 8px #00c3ff22",
  letterSpacing: 1,
};

export const chatTitle = {
  fontWeight: 700,
  fontSize: 20,
  marginBottom: 10,
  color: "#fff",
  textShadow: "0 2px 8px #0002",
};

export const chatBox = {
  flex: 1,
  background: "rgba(40,42,44,0.98)",
  borderRadius: 12,
  boxShadow: "0 2px 12px #0003",
  padding: "18px 18px 18px 18px",
  marginBottom: 0,
  overflowY: "auto",
  minHeight: 0,
  paddingBottom: 70, // добавлено, чтобы не перекрывалось inputRow
  /* Кастомный скроллбар */
  scrollbarWidth: "thin",
  scrollbarColor: "#00c3ff #232526",
};

export const messageRow = isMine => ({
  display: "flex",
  justifyContent: isMine ? "flex-end" : "flex-start",
  marginBottom: 10,
});

export const message = isMine => ({
  marginBottom: 0,
  padding: "7px 12px",
  borderRadius: 8,
  background: isMine ? "linear-gradient(90deg,#00c3ff99,#3a7bd599)" : "rgba(60,62,64,0.85)",
  color: "#fff",
  boxShadow: "0 1px 4px #0002",
  fontSize: 15,
  wordBreak: "break-word",
  maxWidth: "70%",
  alignSelf: isMine ? "flex-end" : "flex-start",
  textAlign: isMine ? "right" : "left",
});

export const messageSender = {
  fontWeight: 600,
  color: "#00c3ff",
  marginRight: 6,
};

export const inputRow = {
  display: "flex",
  alignItems: "center",
  gap: 8,
  marginTop: 8,
  marginBottom: 8,
  position: "sticky", // закрепить
  bottom: 0,
  background: "rgba(40,42,44,0.98)", // совпадает с chatBox
  zIndex: 2,
  padding: "8px 0 8px 0",
};

export const input = {
  flex: 1,
  borderRadius: 8,
  border: "1px solid #3a7bd5",
  padding: "10px 14px",
  fontSize: 15,
  background: "#232526",
  color: "#fff",
  outline: "none",
  transition: "border 0.2s",
};

export const sendBtn = {
  background: "linear-gradient(90deg,#00c3ff,#3a7bd5)",
  color: "#fff",
  border: "none",
  borderRadius: 8,
  padding: "10px 22px",
  fontWeight: 600,
  fontSize: 15,
  cursor: "pointer",
  boxShadow: "0 2px 8px #00c3ff33",
  transition: "background 0.2s, box-shadow 0.2s",
};

export const typing = {
  color: "#b2bec3",
  fontStyle: "italic",
  fontSize: 14,
  margin: "0",
  minHeight: 18,
  background: "none", // убираем фон
  borderRadius: 0,    // убираем скругления
  boxShadow: "none",  // убираем тень
};

export const authContainer = {
  margin: "auto",
  background: "rgba(30,32,34,0.98)",
  borderRadius: 16,
  boxShadow: "0 2px 16px #00c3ff33",
  padding: "40px 32px",
  minWidth: 320,
  maxWidth: 400,
  display: "flex",
  flexDirection: "column",
  alignItems: "center",
};

export const authTitle = {
  fontWeight: 700,
  fontSize: 22,
  marginBottom: 18,
  color: "#00c3ff",
  textShadow: "0 2px 8px #0002",
};

export const authInput = {
  width: "100%",
  borderRadius: 8,
  border: "1px solid #3a7bd5",
  padding: "10px 14px",
  fontSize: 15,
  background: "#232526",
  color: "#fff",
  outline: "none",
  marginBottom: 12,
  transition: "border 0.2s",
};

export const authBtn = {
  width: "100%",
  background: "linear-gradient(90deg,#00c3ff,#3a7bd5)",
  color: "#fff",
  border: "none",
  borderRadius: 8,
  padding: "10px 0",
  fontWeight: 600,
  fontSize: 16,
  cursor: "pointer",
  marginBottom: 10,
  boxShadow: "0 2px 8px #00c3ff33",
  transition: "background 0.2s, box-shadow 0.2s",
};

export const switchBtn = {
  width: "100%",
  background: "none",
  color: "#00c3ff",
  border: "1px solid #00c3ff",
  borderRadius: 8,
  padding: "10px 0",
  fontWeight: 600,
  fontSize: 16,
  cursor: "pointer",
  marginBottom: 0,
  transition: "background 0.2s, color 0.2s",
};

export const error = {
  color: "#ff7675",
  marginBottom: 10,
  fontWeight: 500,
  fontSize: 15,
  textAlign: "center",
};

export const profileBtnBox = {
  position: "absolute",
  left: 18,
  bottom: 70,
  zIndex: 10,
};

export const profileBtn = {
  background: "none",
  border: "none",
  padding: 0,
  cursor: "pointer",
  borderRadius: "50%",
  width: 44,
  height: 44,
  display: "flex",
  alignItems: "center",
  justifyContent: "center",
  boxShadow: "0 2px 8px #00c3ff33",
  transition: "box-shadow 0.2s",
};

export const profilePopup = {
  position: "absolute",
  left: 54,
  bottom: 70,
  minWidth: 220,
  background: "rgba(30,32,34,0.99)",
  color: "#fff",
  borderRadius: 14,
  boxShadow: "0 4px 24px #00c3ff44",
  padding: "22px 22px 16px 22px",
  zIndex: 100,
  fontSize: 15,
  display: "flex",
  flexDirection: "column",
  gap: 8,
  // Убираем animation, используем transition в компоненте
  // animation: "fadeInProfile 0.18s",
  willChange: "transform, opacity",
};

export const profileTitle = {
  fontWeight: 700,
  fontSize: 18,
  color: "#00c3ff",
  marginBottom: 8,
  letterSpacing: 1,
};

export const profileField = {
  marginBottom: 2,
};

export const profileLabel = {
  color: "#00c3ff",
  fontWeight: 600,
  marginRight: 8,
};

export const profileCloseBtn = {
  marginTop: 10,
  alignSelf: "flex-end",
  background: "none",
  color: "#00c3ff",
  border: "1px solid #00c3ff",
  borderRadius: 8,
  padding: "6px 18px",
  fontWeight: 600,
  fontSize: 15,
  cursor: "pointer",
  transition: "background 0.2s, color 0.2s",
};

export const profileEditBtn = {
  background: "linear-gradient(90deg,#00c3ff,#3a7bd5)",
  color: "#fff",
  border: "none",
  borderRadius: 8,
  padding: "8px 18px",
  fontWeight: 600,
  fontSize: 15,
  cursor: "pointer",
  marginTop: 8,
  marginBottom: 0,
  boxShadow: "0 2px 8px #00c3ff33",
  transition: "background 0.2s, box-shadow 0.2s",
};

export const profileInput = {
  borderRadius: 8,
  border: "1px solid #3a7bd5",
  padding: "7px 10px",
  fontSize: 15,
  background: "#232526",
  color: "#fff",
  outline: "none",
  marginLeft: 8,
  minWidth: 80,
  marginBottom: 2,
  transition: "border 0.2s",
};

// Можно добавить глобальные стили, если используется styled-components или emotion:
export const globalStyles = {
  html: {
    height: "100%",
    width: "100%",
    margin: 0,
    padding: 0,
  },
  body: {
    height: "100%",
    width: "100%",
    margin: 0,
    padding: 0,
    overflow: "hidden",
    background: "none",
  },
  "#root": {
    height: "100%",
    width: "100%",
  },
  /* Кастомный скроллбар только для chatBox */
  ".chat-box::-webkit-scrollbar": {
    width: "8px",
    background: "#232526",
    borderRadius: "8px",
  },
  ".chat-box::-webkit-scrollbar-thumb": {
    background: "linear-gradient(120deg, #00c3ff 0%, #3a7bd5 100%)",
    borderRadius: "8px",
  },
  ".chat-box::-webkit-scrollbar-thumb:hover": {
    background: "linear-gradient(120deg, #3a7bd5 0%, #00c3ff 100%)",
  },
};
