x("span",{role:"img","aria-label":"file",children:"\uD83D\uDCCE"}),/*#__PURE__*/_jsx("span",{style:{color:"#fff",fontSize:14,wordBreak:"break-all"},children:fileToSend.name}),/*#__PURE__*/_jsx("button",{style:{marginLeft:"auto",background:"none",border:"none",color:"#ff7675",fontWeight:700,fontSize:16,cursor:"pointer"},title:"\u0423\u0434\u0430\u043B\u0438\u0442\u044C \u0444\u0430\u0439\u043B",onClick:()=>{setFileToSend(null);setFilePreviewUrl(null);if(fileInputRef.current)fileInputRef.current.value="";},children:"\u2715"})]}),modalMedia&&/*#__PURE__*/_jsx("div",{style:{position:"fixed",left:0,top:0,width:"100vw",height:"100vh",background:"rgba(0,0,0,0.7)",zIndex:9999,display:"flex",alignItems:"center",justifyContent:"center"},onClick:()=>setModalMedia(null),children:/*#__PURE__*/_jsxs("div",{style:{background:"#232526",borderRadius:12,padding:24,maxWidth:"90vw",maxHeight:"90vh",boxShadow:"0 4px 32px #000a",display:"flex",flexDirection:"column",alignItems:"center",position:"relative"},onClick:e=>e.stopPropagation(),children:[/*#__PURE__*/_jsx("button",{style:{position:"absolute",top:8,right:12,background:"none",border:"none",color:"#fff",fontSize:28,fontWeight:700,cursor:"pointer",zIndex:2},onClick:()=>setModalMedia(null),title:"\u0417\u0430\u043A\u0440\u044B\u0442\u044C",children:"\u2715"}),modalMedia.type==="image"?/*#__PURE__*/_jsx("img",{src:modalMedia.url,alt:modalMedia.name,style:{maxWidth:"70vw",maxHeight:"70vh",borderRadius:10,marginBottom:16}}):modalMedia.type==="video"?/*#__PURE__*/_jsx("video",{src:modalMedia.url,controls:true,autoPlay:true,style:{maxWidth:"70vw",maxHeight:"70vh",borderRadius:10,marginBottom:16,background:"#000"}}):null,/*#__PURE__*/_jsx("div",{style:{color:"#fff",fontSize:16,marginBottom:8,wordBreak:"break-all"},children:modalMedia.name}),/*#__PURE__*/_jsx("button",{style:{background:"linear-gradient(90deg,#00c3ff,#3a7bd5)",color:"#fff",border:"none",borderRadius:8,padding:"10px 22px",fontWeight:600,fontSize:15,cursor:"pointer",boxShadow