                        await axios.patch(
                          `${API_URL}/profile`,
                          { avatarUrl: uploadRes.data.url },
                          { headers: { Authorization: `Bearer ${token}` } }
                        );
                        // –ü–æ–ª—É—á–∞–µ–º —Å–≤–µ–∂–∏–π –ø—Ä–æ—Ñ–∏–ª—å
