          />
          <button
            style={chatStyles.sendBtn}
            onClick={handleSend}
            disabled={!selectedChannel || (!input.trim() && !fileToSend)}
          >
            –û—Ç–ø—Ä–∞–≤–∏—Ç—å
          </button>
        </div>
        {/* –ü—Ä–µ–≤—å—é –≤—ã–±—Ä–∞–Ω–Ω–æ–≥–æ —Ñ–∞–π–ª–∞ –ø–µ—Ä–µ–¥ –æ—Ç–ø—Ä–∞–≤–∫–æ–π */}
        {fileToSend && (
          <div style={{
            margin: "0 0 8px 0",
            padding: "6px 10px",
            background: "#35363a",
            borderRadius: 8,
            display: "flex",
            alignItems: "center",
            gap: 10,
            maxWidth: 320
          }}>
            {fileToSend.type.startsWith("image/") && filePreviewUrl && (
              <img src={filePreviewUrl} alt="preview" style={{ maxWidth: 48, maxHeight: 48, borderRadius: 6 }} />
            )}
            {fileToSend.type.startsWith("video/") && filePreviewUrl && (
              <video src={filePreviewUrl} style={{ maxWidth: 48, maxHeight: 48, borderRadius: 6 }} />
            )}
            {!fileToSend.type.startsWith("image/") && !fileToSend.type.startsWith("video/") && (
              <span role="img" aria-label="file">üìé</span>
            )}
            <span style={{ color: "#fff", fontSize: 14, wordBreak: "break-all" }}>{fileToSend.name}</span>
            <button
              style={{
                marginLeft: "auto",
                background: "none",
                border: "none",
                color: "#ff7675",
                fontWeight: 700,
                fontSize: 16,
                cursor: "pointer"
              }}
              title="–£–¥–∞–ª–∏—Ç—å —Ñ–∞–π–ª"
              onClick={() => {
                setFileToSend(null);
                setFilePreviewUrl(null);
                if (fileInputRef.current) fileInputRef.current.value = "";
              }}
            >
              ‚úï
            </button>
          </div>
        )}
        {/* –ú–æ–¥–∞–ª—å–Ω–æ–µ –æ–∫–Ω–æ –¥–ª—è –ø—Ä–æ—Å–º–æ—Ç—Ä–∞ —Ñ–æ—Ç–æ/–≤–∏–¥–µ–æ */}
        {modalMedia && (
          <div
            style={{
              position: "fixed",
              left: 0,
              top: 0,
              width: "100vw",
              height: "100vh",
              background: "rgba(0,0,0,0.7)",
              zIndex: 9999,
              display: "flex",
              alignItems: "center",
              justifyContent: "center"
            }}
            onClick={() => setModalMedia(null)}
          >
            <div
              style={{
                background: "#232526",
                borderRadius: 12,
                padding: 24,
                maxWidth: "90vw",
                maxHeight: "90vh",
                boxShadow: "0 4px 32px #000a",
                display: "flex",
                flexDirection: "column",
                alignItems: "center",
                position: "relative"
              }}
              onClick={e => e.stopPropagation()}
            >
              <button
                style={{
                  position: "absolute",
                  top: 8,
                  right: 12,
                  background: "none",
                  border: "none",
                  color: "#fff",
                  fontSize: 28,
                  fontWeight: 700,
                  cursor: "pointer",
                  zIndex: 2
                }}
                onClick={() => setModalMedia(null)}
                title="–ó–∞–∫—Ä—ã—Ç—å"
              >‚úï</button>
              {modalMedia.type === "image" ? (
                <img
                  src={modalMedia.url}
                  alt={modalMedia.name}
                  style={{ maxWidth: "70vw", maxHeight: "70vh", borderRadius: 10, marginBottom: 16 }}
                />
              ) : modalMedia.type === "video" ? (
                <video
                  src={modalMedia.url}
                  controls
                  autoPlay
                  style={{ maxWidth: "70vw", maxHeight: "70vh", borderRadius: 10, marginBottom: 16, background: "#000" }}
                />
              ) : null}
              <div style={{ color: "#fff", fontSize: 16, marginBottom: 8, wordBreak: "break-all" }}>
                {modalMedia.name}
              </div>
              <button
                style={{
                  background: "linear-gradient(90deg,#00c3ff,#3a7bd5)",
                  color: "#fff",
                  border: "none",
                  borderRadius: 8,
                  padding: "10px 22px",
                  fontWeight: 600,
                  fontSize: 15,
                  cursor: "pointer",
                  boxShadow: "0 2px 8px #00c3ff33",
                  textDecoration: "none"
                }}
                onClick={async e => {
                  e.stopPropagation();
                  try {
                    const response = await fetch(modalMedia.url, { credentials: "same-origin" });
                    const blob = await response.blob();
                    const url = window.URL.createObjectURL(blob);
                    const a = document.createElement("a");
                    a.href = url;
                    a.download = modalMedia.name || "file";
                    document.body.appendChild(a);
                    a.click();
                    setTimeout(() => {
                      window.URL.revokeObjectURL(url);
                      a.remove();
                    }, 200);
                  } catch (err) {
                    alert("–û—à–∏–±–∫–∞ —Å–∫–∞—á–∏–≤–∞–Ω–∏—è —Ñ–∞–π–ª–∞");
                  }
                }}
              >
                –°–∫–∞—á–∞—Ç—å
              </button>
            </div>
          </div>
        )}
      </div>
    </div>
  );
}

export default App;
