import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';
import { io } from 'socket.io-client';

const API = 'http://localhost:5000/api';

function App() {
  const [token, setToken] = useState(localStorage.getItem('token'));
  const [channels, setChannels] = useState([]);
  const [current, setCurrent] = useState(null);
  const [messages, setMessages] = useState([]);
  const [text, setText] = useState('');
  const [typing, setTyping] = useState('');
  const socket = useRef(null);

  useEffect(() => {
    if (token) {
      axios.get(`${API}/channels`, { headers: { Authorization: `Bearer ${token}` } })
        .then(res => setChannels(res.data));
      socket.current = io('http://localhost:5000', { auth: { token } });
      socket.current.on('message', msg => setMessages(m => [...m, msg]));
      socket.current.on('typing', data => setTyping(`${data.user} печатает...`));
    }
    return () => socket.current?.disconnect();
  }, [token]);

  useEffect(() => {
    if (current && token) {
      axios.get(`${API}/messages/${current}`, { headers: { Authorization: `Bearer ${token}` } })
        .then(res => setMessages(res.data));
      socket.current.emit('join', current);
    }
  }, [current, token]);

  const send = () => {
    socket.current.emit('message', { text, sender: 'Вы', channel: current });
    setText('');
  };

  const handleTyping = () => {
    socket.current.emit('typing', { channel: current });
  };

  if (!token) {
    // Простая форма входа/регистрации
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [isReg, setIsReg] = useState(false);
    const submit = async () => {
      if (isReg) await axios.post(`${API}/register`, { username, password });
      const res = await axios.post(`${API}/login`, { username, password });
      localStorage.setItem('token', res.data.token);
      setToken(res.data.token);
    };
    return (
      <div>
        <h2>{isReg ? 'Регистрация' : 'Вход'}</h2>
        <input placeholder="Имя" value={username} onChange={e => setUsername(e.target.value)} />
        <input placeholder="Пароль" type="password" value={password} onChange={e => setPassword(e.target.value)} />
        <button onClick={submit}>{isReg ? 'Зарегистрироваться' : 'Войти'}</button>
        <button onClick={() => setIsReg(r => !r)}>{isReg ? 'Войти' : 'Регистрация'}</button>
      </div>
    );
  }

  return (
    <div style={{ display: 'flex' }}>
      <div style={{ width: 200 }}>
        <h3>Каналы</h3>
        {channels.map(c => (
          <div key={c._id} onClick={() => setCurrent(c._id)} style={{ cursor: 'pointer', background: current === c._id ? '#eee' : '' }}>
            {c.name}
          </div>
        ))}
      </div>
      <div style={{ flex: 1, marginLeft: 20 }}>
        <h3>Чат</h3>
        <div style={{ height: 300, overflowY: 'auto', border: '1px solid #ccc' }}>
          {messages.map(m => (
            <div key={m._id}><b>{m.sender}:</b> {m.text} {m.fileUrl && <a href={m.fileUrl}>[файл]</a>}</div>
          ))}
        </div>
        <div>{typing}</div>
        <input value={text} onChange={e => setText(e.target.value)} onKeyDown={handleTyping} />
        <button onClick={send}>Отправить</button>
      </div>
    </div>
  );
}

export default App;
