import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';
import { io } from 'socket.io-client';

const API = 'http://localhost:5000/api';

function App() {
  const [token, setToken] = useState(localStorage.getItem('token'));
  const [channels, setChannels] = useState([]);
  const [current, setCurrent] = useState(null);
  const [messages, setMessages] = useState([]);
  const [text, setText] = useState('');
  const [typing, setTyping] = useState('');
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [isReg, setIsReg] = useState(false);
  const [loginError, setLoginError] = useState('');
  const [channelName, setChannelName] = useState('');
  const [showCreate, setShowCreate] = useState(false);
  const socket = useRef(null);

  useEffect(() => {
    if (token) {
      axios.get(`${API}/channels`, { headers: { Authorization: `Bearer ${token}` } })
        .then(res => setChannels(res.data));
      socket.current = io('http://localhost:5000', { auth: { token } });
      socket.current.on('message', msg => setMessages(m => [...m, msg]));
      socket.current.on('typing', data => setTyping(`${data.user} печатает...`));
    }
    return () => socket.current?.disconnect();
  }, [token]);

  useEffect(() => {
    if (current && token) {
      axios.get(`${API}/messages/${current}`, { headers: { Authorization: `Bearer ${token}` } })
        .then(res => setMessages(res.data));
      socket.current.emit('join', current);
    }
  }, [current, token]);

  const send = () => {
    if (!current || !text.trim()) return;
    const msg = { text, sender: 'Вы', channel: current };
    socket.current.emit('message', msg);
    setText('');
  };

  const handleTyping = (e) => {
    if (!current) return;
    socket.current.emit('typing', { channel: current });
    if (e.key === 'Enter') {
      send();
    }
  };

  const createChannel = async () => {
    if (!channelName.trim()) return;
    try {
      const res = await axios.post(`${API}/channels`, {
        name: channelName,
        members: [JSON.parse(atob(token.split('.')[1])).username]
      }, { headers: { Authorization: `Bearer ${token}` } });
      setChannels(chs => [...chs, res.data]);
      setCurrent(res.data._id);
      setChannelName('');
      setShowCreate(false);
    } catch (e) {
      alert('Ошибка создания канала');
    }
  };

  if (!token) {
    const submit = async () => {
      try {
        setLoginError('');
        if (isReg) await axios.post(`${API}/register`, { username, password });
        const res = await axios.post(`${API}/login`, { username, password });
        localStorage.setItem('token', res.data.token);
        setToken(res.data.token);
      } catch (e) {
        setLoginError('Неверный логин или пароль, либо пользователь не зарегистрирован');
      }
    };
    return (
      <div>
        <h2>{isReg ? 'Регистрация' : 'Вход'}</h2>
        {loginError && <div style={{color: 'red'}}>{loginError}</div>}
        <input placeholder="Имя" value={username} onChange={e => setUsername(e.target.value)} />
        <input placeholder="Пароль" type="password" value={password} onChange={e => setPassword(e.target.value)} />
        <button onClick={submit}>{isReg ? 'Зарегистрироваться' : 'Войти'}</button>
        <button onClick={() => setIsReg(r => !r)}>{isReg ? 'Войти' : 'Регистрация'}</button>
      </div>
    );
  }

  return (
    <div style={{ display: 'flex' }}>
      <div style={{ width: 220, paddingRight: 10, borderRight: '1px solid #ccc' }}>
        <h3>Каналы</h3>
        {channels.length === 0 ? (
          <div>
            <div style={{ color: '#888', marginBottom: 8 }}>Нет доступных каналов</div>
            {showCreate ? (
              <div>
                <input
                  placeholder="Название канала"
                  value={channelName}
                  onChange={e => setChannelName(e.target.value)}
                  style={{ width: '90%' }}
                />
                <button onClick={createChannel} style={{ marginTop: 4 }}>Создать</button>
                <button onClick={() => setShowCreate(false)} style={{ marginLeft: 4 }}>Отмена</button>
              </div>
            ) : (
              <button onClick={() => setShowCreate(true)}>Создать канал</button>
            )}
          </div>
        ) : (
          <>
            {channels.map(c => (
              <div key={c._id} onClick={() => setCurrent(c._id)} style={{ cursor: 'pointer', background: current === c._id ? '#eee' : '', padding: '2px 0' }}>
                {c.name}
              </div>
            ))}
            <button style={{ marginTop: 10 }} onClick={() => setShowCreate(s => !s)}>
              {showCreate ? 'Скрыть создание' : 'Создать канал'}
            </button>
            {showCreate && (
              <div style={{ marginTop: 4 }}>
                <input
                  placeholder="Название канала"
                  value={channelName}
                  onChange={e => setChannelName(e.target.value)}
                  style={{ width: '90%' }}
                />
                <button onClick={createChannel} style={{ marginTop: 4 }}>Создать</button>
              </div>
            )}
          </>
        )}
      </div>
      <div style={{ flex: 1, marginLeft: 20 }}>
        <h3>Чат</h3>
        <div style={{ height: 300, overflowY: 'auto', border: '1px solid #ccc' }}>
          {messages.map(m => (
            <div key={m._id}><b>{m.sender}:</b> {m.text} {m.fileUrl && <a href={m.fileUrl}>[файл]</a>}</div>
          ))}
        </div>
        <div>{typing}</div>
        <input
          value={text}
          onChange={e => setText(e.target.value)}
          onKeyDown={handleTyping}
          disabled={!current}
          placeholder={current ? "Введите сообщение..." : "Выберите канал"}
        />
        <button onClick={send} disabled={!current || !text.trim()}>Отправить</button>
      </div>
    </div>
  );
}

export default App;
